import React from 'react'
import { nanoid } from "nanoid";
import { useForm } from "react-hook-form";
import { toast } from "react-toastify";

export default function TaskHookForm({ kisiler, submitFn }) {
  //3- useForm ile tanımla
  const {
    register,
    handleSubmit,
    formState: { errors, isValid },
  } = useForm({
    defaultValues: {
      title: "",
      description: "",
      people: [],
    },
    mode: "onChange",
  });

  const onFormSubmit = (formData, e) => {
    e.target.reset();
    console.log(formData);
    submitFn({
      ...formData,
      id: nanoid(5),
      status: "yapılacak",
    });
    notify(taskId);
  };
  const notify = () => {
    toast(`XX numaralı iş eklenmiştir.`);
  };
  return (
    <form className="taskForm" onSubmit={handleSubmit(onFormSubmit)}>
          <div className="form-line">
            <label className="input-label" htmlFor="title">
              Başlık
            </label>
            <input
              className="input-text"
              id="title"
              name="title"
              type="text"
              {...register("title", {
                required: "Lütfen başlık yazınız",
                minLength: {
                  value: 3,
                  message: "En az 3 karakter giriniz.",
                },
              })}
            />
            {errors.title && <div> {errors.title.message} </div>}

          </div>

          <div className="form-line">
            <label className="input-label" htmlFor="description">
              Açıklama
            </label>
            <textarea
              className="input-textarea"
              rows="3"
              id="description"
              name="description"
              {...register("description", {
                required: "Lütfen açıklama yazınız",
                minLength: {
                  value: 3,
                  message: "En az 3 karakter giriniz.",
                },
              })}
            ></textarea>
            {errors.description && <div> {errors.description.message} </div>}
          </div>

          <div className="form-line">
            <label className="input-label">İnsanlar</label>
            <div>
              {kisiler.map((p) => (
                <label className="input-checkbox" key={p}>
                  <input
                    type="checkbox"
                    name="people"
                    value={p}
                    {...register("people", {
                      required: "Lütfen kişi seçin",
                    })}
                  />
                  {p}
                </label>
              ))}
            </div>
            {errors.people && <div> {errors.people.message} </div>}
          </div>

          <div className="form-line">
            <button
              className="submit-button"
              type="submit"
              disabled={!isValid}
            >
              Kaydet
            </button>
          </div>
    </form>  
    )
}
